function User({ userId, email, firstName, lastName, phoneNumber }) {
  return (
    <div>
      <h1>{userId}</h1>
      <p>{email}</p>
      <p>{firstName}</p>
      <p>{lastName}</p>
      <p>{phoneNumber}</p>
    </div>
  );
}

export function Users() {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  useEffect(() => {
    setLoading(true);
    fetch(`http://localhost:8080/api/users`)
      .then((response) => response.json())
      .then(setData)
      .then(() => setLoading(false))
      .catch(setError);
    console.log(response);
  }, []);

  if (loading) return <h1>Loading...</h1>;
  if (error) return <per>{JSON.stringify(error)}</per>;
  if (!data) return null;
  return <User email={data.email}></User>;
}
