      //           <td>{item.category}</td>
      //           <td>{item.productDescription}</td>
      //           <td>{item.pricePerUnit}</td>
      //           <td>{item.availableStock}</td>
      //           <td>{item.reservedStock}</td>
      //           <td>{item.shippedStock}</td>
      //           <td>
      //             <img src={item.imageUrl} height={100} width={100} />
      //           </td>
      //           <td>
      //             <EditOutlined />
      //             <DeleteOutlined
      //               onClick={() => {
      //                 onDeleteProduct();
